sysauto_script.script:
var rightNow = new GlideDateTime();
var twentyFourHoursAhead = gs.hoursAgo(-24); // Negative value to represent hours into the future

// Filter for records that are within 24 hours into the future of the current time at which the job gets executed
var upcomingNITasks = new GlideRecord('x_58872_needit_needit_task');
upcomingNITasks.addQuery('due_date', '>=', rightNow);
upcomingNITasks.addQuery('due_date', '<=', twentyFourHoursAhead);
upcomingNITasks.addQuery('state', '!=', 3);

upcomingNITasks.query();
while(upcomingNITasks.next()) {
	gs.info("NeedIt Task record due within 24 hours = " + upcomingNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',upcomingNITasks,upcomingNITasks.number,gs.getUserName());
}
